import { readFileSync, writeFileSync } from "fs"

const output = "src/package-info.ts"
const pkg = readFileSync("./package.json")
const { description, version } = JSON.parse(pkg.toString())

writeFileSync("src/package-info.ts", `
// This file is auto generated by bin/generate-file-info.ts
// DO NOT EDIT

export const description = \`${description}\`
export const version = \`${version}\`
`.trim())

console.log(`Wrote contents to ${output}`)
